pragma solidity ^0.4.18;

import './MNS.sol';
import './MithrilToken.sol';

/**
 * A registrar that allocates subdomains to the first person to claim them.
 */
contract MNSRegistrar {
    MNS mns;
    MithrilToken mithrilToken;
    bytes32 rootNode;

    modifier only_owner(string subnode) {
        address currentOwner = mns.owner(keccak256(rootNode, keccak256(subnode)));
        require(currentOwner == 0 || currentOwner == msg.sender);
        _;
    }

    /**
     * Constructor.
     */
    function MNSRegistrar(MNS mnsAddr, MithrilToken mithrilTokenAddr, bytes32 node) public {
        mns = mnsAddr;
        mithrilToken = mithrilTokenAddr;
        rootNode = node;
    }

    /**
     * transfer from money to contract
     */

    /**
     * Register a name, or change the owner of an existing registration.
     * @param subnode The hash of the label to register.
     * @param owner The address of the new owner.
     */
    function register(string subnode, address owner, uint256 price) public {
        // direct purchase
        // invoke MithrilToken to tranfer token to 
        // add logic for domain
        //uint256 allowance = mithrilToken.allowance[msg.sender][0x0];
        //mithrilToken.transferFrom(msg.sender, address(this), 1);
        mns.setSubnodeOwner(rootNode, keccak256(subnode), owner);
    }

    /**
     * Process
     * 1. User send MithrilToken approve to MNSRegistrar
     * 2. User send MNSRegistrar register claim MNS
     */
}

